package com.galang.smartalarm.data.localimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabaseimport com.galang.smartalarm.data.Alarm@Database(entities = [Alarm::class], version = 1)abstract class AlarmDB : RoomDatabase(){    abstract fun alarmDao() : AlarmDao    companion object{        @Volatile        var instace : AlarmDB? = null        @JvmStatic        fun getDatabase(context: Context) : AlarmDB{            if (instace == null){                synchronized(AlarmDB::class.java){                    instace = Room.databaseBuilder(                        context, AlarmDB::class.java, "smart_alarm.db"                    ).fallbackToDestructiveMigration()                        .build()                }            }            return instace as AlarmDB        }    }}