package com.galang.smartalarm.fragmentimport android.app.DatePickerDialogimport android.app.Dialogimport android.content.Contextimport android.os.Bundleimport android.util.Logimport android.widget.DatePickerimport androidx.fragment.app.DialogFragmentimport java.util.*class DatePickerFragment : DialogFragment(),DatePickerDialog.OnDateSetListener { //Dialog// fragment untuk menggabungkan si fragment sama si activity    private var dialogListener: DateDialogListener? = null    //onAttach dan onDetach buat mastiin biar gak doble keluarnya    override fun onAttach(context: Context) {        super.onAttach(context)        dialogListener = context as DateDialogListener    }    override fun onDetach() {        super.onDetach()        if (dialogListener != null) dialogListener = null    }    //Membuat kotaknya/ display nya    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {        val calendar = Calendar.getInstance()        val year = calendar.get(Calendar.YEAR)        val month = calendar.get(Calendar.MONTH)        val dayOfMounth = calendar.get(Calendar.DAY_OF_MONTH)        return DatePickerDialog(activity as Context, this, year, month, dayOfMounth)    }    //Buat ngeset / naro tanggal yang di pilih sama si pengguna    override fun onDateSet(p0: DatePicker?, year: Int, month: Int, dayOfMounth: Int) {        dialogListener?.onDialogDateSet(tag, year, month, dayOfMounth)        Log.i(tag, "onDateSet: $year $month $dayOfMounth ")    }    //Buat ngambil data yang kita click / Buat di panggil di Activity supaya dapat nilai inputan    // yang sudah di pilih    interface DateDialogListener {        fun onDialogDateSet(tag : String?, year: Int, month: Int, dayOfMounth: Int)    }}