package com.galang.smartalarm.helperimport java.text.SimpleDateFormatimport java.util.*const val TAG_TIME_PICKER = "Time Picker Dialog"fun timeFormatter (hoursOfDay : Int, minute : Int) : String{    val calendar = Calendar.getInstance()    calendar.set(0, 0,0, hoursOfDay, minute)    return SimpleDateFormat("HH:mm", Locale.getDefault()).format(calendar.time)}fun image(){}